import React, { Component } from 'react';
import { Link } from '@reach/router';
import * as api from '../utils/axiosRequests';

class SingleArticle extends Component {
  state = {
    article: {},
    comments: []
  };

  handlePostComment(event) {
    let commentToPost = event.target.value;
    api
      .postComment(commentToPost, this.props.user, this.props.article_id)
      .then(comment => {
        this.setState(currentState => {
          return { comments: [comment, ...this.state.comments] };
        });
      });
  }

  componentDidMount() {
    Promise.all([
      api.getSingleArticle(this.props.article_id),
      api.getArticleComments(this.props.article_id)
    ]).then(resolutions => {
      this.setState({
        article: resolutions[0],
        comments: resolutions[1]
      });
    });
  }

  render() {
    return (
      <div>
        <h2>{this.state.article.title}</h2>
        <Link to={`/${this.state.article.topic}`}>
          <h6>See all {this.state.article.topic} stories</h6>
        </Link>
        <p>{this.state.article.body}</p>
        <h4>Comments</h4>
        <form onSubmit={this.handlePostComment}>
          <h3>Post a comment: </h3>
          <textarea
            name="commentPost"
            id="commentPost"
            cols="30"
            rows="10"
          ></textarea>
          <input type="submit" />
        </form>
        <ul>
          {this.state.comments.map(comment => {
            return (
              <li key={comment.comment_id}>
                <h5>{comment.author}</h5>
                <p>{comment.body}</p>
                <h5>Votes: {comment.votes}</h5>
                <h6>Created at: {comment.created_at}</h6>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}

export default SingleArticle;
