import React, { Component } from 'react';
import * as api from '../utils/axiosRequests';
import ArticleCard from '../components/ArticleCard';

class ArticleList extends Component {
  state = { articles: [] };

  componentDidMount() {
    console.log(this.props.mainFeed);
    api.getAllArticles(this.props.topic).then(articles => {
      this.setState({ articles: articles, mainFeed: this.props.mainFeed });
    });
  }

  componentDidUpdate(prevProps, prevState) {
    if (this.props.topic !== prevProps.topic || this.state.mainFeed) {
      api.getAllArticles(this.props.topic).then(articles => {
        this.setState({
          articles: articles
        });
      });
    }
  }

  switchMainFeedOff() {
    if (this.state.mainFeed) this.setState({ mainFeed: false });
  }

  render() {
    return (
      <div>
        <ul>
          {this.state.articles.map(article => {
            return (
              <ArticleCard
                key={article.article_id}
                title={article.title}
                body={article.body}
                topic={article.topic}
                id={article.article_id}
                mainFeed={this.state.mainFeed}
                switchMainFeedOff={this.switchMainFeedOff}
              />
            );
          })}
        </ul>
      </div>
    );
  }
}

export default ArticleList;
