import React, { Component } from 'react';
import * as api from '../utils/axiosRequests';
import ArticleCard from '../components/ArticleCard';

class ArticleList extends Component {
  state = { articles: [] };

  componentDidMount() {
    api.getAllArticles(this.props.topic).then(articles => {
      this.setState({ articles: articles, mainFeed: this.props.mainFeed });
    });
  }

  componentDidUpdate(prevProps, prevState) {
    if (this.props.topic !== prevProps.topic || this.state.mainFeed) {
      api.getAllArticles(this.props.topic).then(articles => {
        this.setState({
          articles: articles
        });
      });
    }
  }

  handleSelectChange({ target: { value } }) {
    api.getSortedArticles(this.props.topic, value).then(articles => {
      this.setState({ articles: articles });
    });
  }

  render() {
    return (
      <div>
        <aside>
          <h4>Sort by:</h4>
          <select
            name="articleSelector"
            id="articleSelector"
            onChange={this.handleSelectChange}
          >
            <option value="date_created">Date created</option>
            <option value="comment_count">Comment count</option>
            <option value="votes">Votes</option>
          </select>
        </aside>
        <ul>
          {this.state.articles.map(article => {
            return (
              <ArticleCard
                key={article.article_id}
                title={article.title}
                body={article.body}
                topic={article.topic}
                id={article.article_id}
                mainFeed={this.props.mainFeed}
              />
            );
          })}
        </ul>
      </div>
    );
  }
}

export default ArticleList;
