import React, { Component } from 'react';

class Voter extends Component {
  state = {
    votes: NaN
  };
  render() {
    return (
      <div>
        <button onClick={this.handleVote} id="1">
          Upvote
        </button>
        <p>
          {this.state.votes
            ? this.props.article.votes + this.state.votes
            : this.props.article.votes}
        </p>
        <button onClick={this.handleVote} id="-1">
          Downvote
        </button>
      </div>
    );
  }
}

export default Voter;
