import React from 'react';
import { Link } from '@reach/router';

const ArticleCard = ({ title, body, id, topic }) => {
  return (
    <li>
      <Link to={`/${topic}/${id}`}>
        <h4>{title}</h4>
        <Link to={`/${topic}`}>
          <h6>Topic: {topic}</h6>
        </Link>
      </Link>
      <p>{body}</p>
    </li>
  );
};

export default ArticleCard;
