import React from 'react';
import { Link } from '@reach/router';

const ArticleCard = ({
  title,
  body,
  id,
  topic,
  mainFeed,
  switchMainFeedOff
}) => {
  return (
    <li>
      <Link to={`/${topic}/${id}`}>
        <h4>{title}</h4>
      </Link>
      {mainFeed && (
        <Link to={`/${topic}`} onClick={switchMainFeedOff}>
          <h6>Other {topic} stories</h6>
        </Link>
      )}
      <p>{body}</p>
    </li>
  );
};

export default ArticleCard;
