import React, { Component } from 'react';
import { Link } from '@reach/router';
import * as api from '../utils/axiosRequests';

class SingleArticle extends Component {
  state = {
    article: {}
  };

  componentDidMount() {
    let outputArticle = {};
    let outputComments = {};
    api
      .getSingleArticle(this.props.article_id)
      .then(article => {
        outputArticle = article;
      })
      .then(() => {
        api.getArticleComments(this.props.article_id).then(comments => {
          outputComments = comments;
        });
      })
      .then(() => {
        this.setState({
          article: outputArticle,
          comments: outputComments
        });
      });
  }

  render() {
    return (
      <div>
        <h2>{this.state.article.title}</h2>
        <Link to={`/${this.state.article.topic}`}>
          <h6>See all {this.state.article.topic} stories</h6>
        </Link>
        <p>{this.state.article.body}</p>
      </div>
    );
  }
}

export default SingleArticle;
