import React, { Component } from 'react';
import * as api from '../utils/axiosRequests';

class SingleArticle extends Component {
  state = {
    article: {}
  };

  componentDidMount() {
    getSingleArticle().then(article => {
      this.setState({ article: article });
    });
  }

  render() {
    return (
      <div>
        <p></p>
      </div>
    );
  }
}

export default SingleArticle;
